Issue.Description:
``` C#
    using System;
    using System.ComponentModel.Composition;
    using System.Diagnostics.CodeAnalysis;
    using Microsoft.VisualStudio.ProjectSystem;
    using Microsoft.VisualStudio.ProjectSystem.Properties;
    using Microsoft.VisualStudio.ProjectSystem.VS.Properties;
    using VSLangProj;

    /// <summary>
    /// Implementation of the extender CATID provider for CSharp project system.
    /// </summary>
    [Export(typeof(IExtenderCATIDProvider))]
    [AppliesTo(ProjectCapabilities.CSharpOrVisualBasic)]
    internal class CSharpExtenderCATIDProvider : IExtenderCATIDProvider
    {
        /// <inheritdoc />
        public string GetExtenderCATID(ExtenderCATIDType extenderCATIDType, IProjectTree treeNode)
        {
            // TODO: Ensure consistent behavior with legacy project system
            switch (extenderCATIDType)
            {
                // Browse object
                case ExtenderCATIDType.ProjectBrowseObject:
                    return PrjBrowseObjectCATID.prjCATIDCSharpProjectBrowseObject;

                case ExtenderCATIDType.FileBrowseObject:
                    return PrjBrowseObjectCATID.prjCATIDCSharpFileBrowseObject;

                case ExtenderCATIDType.FolderBrowseObject:
                    return PrjBrowseObjectCATID.prjCATIDCSharpFolderBrowseObject;

                case ExtenderCATIDType.ReferenceBrowseObject:
                    return PrjBrowseObjectCATID.prjCATIDCSharpReferenceBrowseObject;

                // Called via DTE
                case ExtenderCATIDType.AutomationProject:
                    return PrjCATID.prjCATIDProject;

                case ExtenderCATIDType.AutomationProjectItem:
                    return PrjCATID.prjCATIDProjectItem;

                // Called via IVsHierarchy.GetProperty
                case ExtenderCATIDType.HierarchyBrowseObject:
                    return PrjBrowseObjectCATID.prjCATIDCSharpProjectBrowseObject;

                case ExtenderCATIDType.HierarchyConfigurationBrowseObject:
                    return PrjBrowseObjectCATID.prjCATIDCSharpProjectConfigBrowseObject;

                case ExtenderCATIDType.HierarchyExtensionObject:
                    return null;
            }

            return null;
        }
    }
```
