        [Fact]
        public void DuplicateInterfaceDetectionWithDifferentTupleNames_02()
        {
            var source = @"
public interface I1<T> { }
public interface I2 : I1<(int a, int b)> { }
public class C1 : I2, I1<(int c, int d)> { }
