```
public class MyClass
{
    public void Test4(IEnumerable<((int, int), string)> en)
    {
        foreach ((ValueTuple<int,int> t, string s) in en)
        {
            var a =t.Item1; // false error CS0170: Use of possibly unassigned field
        }
    }

    public void Test5(IEnumerable<((int, int), string)> en)
    {
